const Graph = ({ xData, yData, color1, color2 }) => {
    // set max range of x and y axis
    const maxX = Math.max(...xData);
    const maxY = Math.max(...yData);

    // calculate scaling factor
    const scale = 300 / Math.max(maxX, maxY);

    // scale data and offset to center
    const scaledXData = xData.map(x => (x * scale) + 150);
    const scaledYData = yData.map(y => 450 - (y * scale));

    // draw x and y axis lines
    const xAxis = <line x1="0" y1="450" x2="600" y2="450" stroke="black" />;
    const yAxis = <line x1="150" y1="0" x2="150" y2="600" stroke="black" />;

    // plot data points
    const plot1 = scaledXData.map((x, i) => (
        <circle key={i} cx={x} cy={scaledYData[i]} r="4" fill={color1} />
    ));
    const plot2 = scaledXData.map((x, i) => (
        <circle key={i} cx={x} cy={scaledYData[i]} r="4" fill={color2} />
    ));

    // draw lines between data points
    const line1 = (
        <polyline
            points={`${scaledXData[0]},${scaledYData[0]} ${scaledXData[1]},${scaledYData[1]} ${scaledXData[2]},${scaledYData[2]} ${scaledXData[3]},${scaledYData[3]}`}
            stroke={color1}
            fill="none"
        />
    );
    const line2 = (
        <polyline
            points={`${scaledXData[0]},${scaledYData[0]} ${scaledXData[1]},${scaledYData[1]} ${scaledXData[2]},${scaledYData[2]} ${scaledXData[3]},${scaledYData[3]}`}
            stroke={color2}
            fill="none"
        />
    );

    return (
        <svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg">
            {xAxis}
            {yAxis}
            {plot1}
            {plot2}
            {line1}
            {line2}
        </svg>
    );
};


function Pointstable(props) {
    return(
        <div className='player_stats wt20_1'>
            <div className='pstat_box ind1'>
                <div className='pstat_header'>
                    <Photocard height={'100px'} p_id={3}/>
                    <div className='pstat_header_info'>
                        <div className='pstat_header_playername'>
                            Virat Kohli
                        </div>
                        <div className='pstat_header_teamname'>
                            🇮🇳 IND
                        </div>
                    </div>
                </div>

            </div>
            <div className='navbar'>

            </div>

        </div>
    );
}
export default Pointstable;
