import './BatsmenItem.css'

function BatsmenItem(props) {

    let class1 = "playername wt20 "+ "ind" + "1"
    let class2 = "batsman_score wt20 " + "ind" + "2"
    let pad = 0
    let character = props.runs.charAt(-1)
    console.log(character)
    if (character == "*") {
        pad = 4
      } else {
        pad = 5
      }

    let padding = "padding-right:" + pad.toString() +";"
    return (
        <div className="batsmen_item">
           <div className={class1}>{props.name}</div>
           <div className={class2}>
                <div className="runs" style={padding}>
                    {props.runs}
                </div>
                <div className="balls">
                    {props.balls}
                </div>
            </div> 
        </div>
    )

    }
export default BatsmenItem;
