import './Scorecard.css'
import '../css/teams.css'
import React, { useState, useEffect } from 'react'; 

function Header(props) {

    let color = props.header.color
    let class2 = "match_scorecard_header1 " + color + "1"
    let class3 = 'match_scorecard_header_4s ' + color + "1"
    let class4 = 'match_scorecard_header_6s ' + color + "1"
    let class5 = 'match_scorecard_header_score ' + color + "2"
    return (
    <div id="match_scorecard_header">
        <div className={class2}>
            {props.header.teamname}
        </div>
        <div className="match_scorecard_header2">
            <div className={class3}>
                Fours: {props.header.fours}
            </div>
            <div className={class4}>
                Sixes: {props.header.sixes}
            </div>
            <div className={class5}>
                <div id="score">
                    {props.header.score}
                </div>
                <div id="overs">
                    ({props.header.overs})
                </div>
            </div>
        </div>

    </div>
    );
}

function ScoreItem(props) {
    let i = props.item
    if (i.not_out==0) {
        let class1 = 'scorecard_item_batsman ' + props.color + "2"
        let class2 = 'scorecard_item_data1 ' + props.color + "2"
        let class3 = 'scorecard_item_data2 ' + props.color + "2"
        let class4 = 'scorecard_item_score ' + props.color + "1"
    }
    else {
    let class1 = 'scorecard_item_batsman ' + props.color + "1"
    let class2 = 'scorecard_item_data1 ' + props.color + "1"
    let class3 = 'scorecard_item_data2 ' + props.color + "1"
    let class4 = 'scorecard_item_score ' + props.color + "2"
    }

    return (
        <div id="scorecard_item">
            <div className={class1}>
                {i.name}
            </div>
            <div className={class2}>
                {i.data2}
            </div>
            <div className={class3}>
                {i.data1}
            </div>
            <div className={class4}>

            </div>
        </div>
    );
}

function Scorecard(props) {
    let url = "http://localhost:8000/match/" + props.m_id + "/"+props.inn_no + "/scorecard"
    // console.log(typeof props.inn_no)
    // let url = "http://localhost:8000/match/13/1/scorecard"
    // console.log(typeof url)
    const [scorecard, setscorecard] = useState(null);

    useEffect(() => {
        const fetchScorecard = async () => {
        const response1 = await fetch(url);
        const jsonscorecard = await response1.json();
        setscorecard(jsonscorecard);
        };

        fetchScorecard();
    }, []);


    if (!scorecard) {
        return <div>Loading...Scorecard</div>;
      }

    console.log(scorecard)
    // const header = scorecard.header
    // console.log(header)
    let class1 = "match_scorecard " + "wt20"
    return (
        <div className={class1}>
            <Header header={scorecard.header}/>
            <div id="match_scorecard_body">
                {scorecard.body.map((item, index) => (
                <ScoreItem key={index} item={item} color={scorecard.header.color} />
                ))}
            </div>
        </div>
        );

    }
export default Scorecard;
